  "chats/getAllchatAsync",
  async () => {
    Axios.get("http://localhost:5000/api/chat", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => {
        console.log(res.data);
        return res.data;
      })
      .catch((error) => {
        console.error(error);
      });
    const chats = response.data.chats;
    console.log(chats);
    return { chats };
  }
);
