import React, { useState } from "react";
import "../scss/components/login_signup.css";

const SignUp = (props) => {
  const [isLogin, setLogin] = useState(false);
  const [isSignup, setSignup] = useState(true);
  const [loginData, setLoginData] = useState({
    emailLogin: "",
    passwordLogin: "",
  });
  const [signupData, setSignupData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPasword: "",
  });
  function handleLogin() {
    setLogin(true);
    setSignup(false);
  }
  function handleSignup() {
    setSignup(true);
    setLogin(false);
  }
  const handleSignupData = (e) => {
    setSignupData((prevData) => {
      return {
        ...prevData,
        [e.target.name]: e.target.value,
      };
    });
  };
  const handleLoginData = (e) => {
    setLoginData((prevData) => {
      return {
        ...prevData,
        [e.target.name]: e.target.value,
      };
    });
  };
  console.log(loginData);
  console.log(signupData);
  return (
    <div>
      <div className="Login">
        <div className="title-text">
          {isSignup && <div className="title login">Sign Up</div>}
          {isLogin && <div className="title login">Login</div>}
          {/* <div className="title signup">Signup</div> */}
        </div>
        <div className="form-container">
          <div class="slide-controls">
            <button className="slide" id="login" onClick={handleLogin}>
              Login
            </button>
            <button className="slide" id="signup" onClick={handleSignup}>
              Sign up
            </button>
          </div>
          {isSignup && (
            <form className="form-inner">
              <input
                name="name"
                value={signupData.name}
                className="field"
                placeholder="Name"
                onChange={handleSignupData}
                isRequire
              />
              <input
                name="email"
                value={signupData.email}
                className="field"
                placeholder="Email Address"
                onChange={handleSignupData}
              />
              <input
                name="password"
                value={signupData.password}
                className="field"
                placeholder="Password"
                onChange={handleSignupData}
              />
              <input
                name="confirmPasword"
                value={signupData.confirmPasword}
                className="field"
                placeholder="Confirm Password"
                onChange={handleSignupData}
              />
              {/* <a href="#">Forgot password?</a> */}
              <button>Sign Up</button>
            </form>
          )}
        </div>

        {isLogin && (
          <div className="Login">
            <form className="form-inner">
              <input
                name="emailLogin"
                value={loginData.emailLogin}
                className="field"
                placeholder="Email Address"
                onChange={handleLoginData}
              />
              <input
                name="passwordLogin"
                value={loginData.passwordLogin}
                className="field"
                placeholder="Password"
                onChange={handleLoginData}
              />
              {/* <a href="#">Forgot password?</a> */}
              <button>Login</button>
            </form>
            <h5>
              Not a member? <a href="">Signup Now</a>
            </h5>
          </div>
        )}
      </div>
    </div>
  );
};

export default SignUp;
